import React, { useState } from 'react';
import axios from 'axios';

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [showRegister, setShowRegister] = useState(false);

  const handleLoginSubmit = async (event) => {
    event.preventDefault();
    try {
      // Replace this URL with your actual login endpoint
      const response = await axios.post('http://localhost:5000/api/login', {
        username,
        password
      });
      // Handle successful login here
      console.log('Login successful:', response.data);
    } catch (error) {
      // Handle errors here
      console.error('Login failed:', error);
      setError('Invalid username or password');
    }
  };

  const handleRegisterSubmit = async (event) => {
    event.preventDefault();
    if (password !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }
    try {
      // Replace this URL with your actual registration endpoint
      const response = await axios.post('http://localhost:5000/api/register', {
        username,
        password
      });
      // Handle successful registration here
      console.log('Registration successful:', response.data);
    } catch (error) {
      // Handle errors here
      console.error('Registration failed:', error);
      setError('Failed to register');
    }
  };

  const [confirmPassword, setConfirmPassword] = useState('');

  return (
    <div>
      <h1>{showRegister ? 'Register' : 'Login'}</h1>
      <form onSubmit={showRegister ? handleRegisterSubmit : handleLoginSubmit}>
        <div>
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {showRegister && (
          <div>
            <label htmlFor="confirmPassword">Confirm Password:</label>
            <input
              type="password"
              id="confirmPassword"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              required
            />
          </div>
        )}
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <button type="submit">{showRegister ? 'Register' : 'Login'}</button>
      </form>
      <button onClick={() => setShowRegister(!showRegister)}>
        {showRegister ? 'Back to Login' : 'Register'}
      </button>
    </div>
  );
};

export default LoginPage;
